import React from "react";

import NavigationButton from "./navigation-button"
import TextTitle from "./text-title";
import styles from "./navigation.module.css"
import {Twitter, Home, Explore, Notification, Messages, Bookmark, Lists, Profile, More} from "./icons"

const MENU = [
  {
    key: "home",
    icon: <Home />,
    title: "Home",
    notify: 0
  },
  {
    key: "explore",
    icon: <Explore />,
    title: "Explore",
    notify: 0
  },
  {
    key: "notification",
    icon: <Notification />,
    title: "Notification",
    notify: 2
  },
  {
    key: "messages",
    icon: <Messages />,
    title: "Messages",
    notify: 0
  },
  {
    key: "bookbark",
    icon: <Bookmark />,
    title: "Bookmark",
    notify: 0
  },
  {
    key: "lists",
    icon: <Lists />,
    title: "Lists",
    notify: 0
  },
  {
    key: "profile",
    icon: <Profile />,
    title: "Profile",
    notify: 0
  },
  {
    key: "more",
    icon: <More />,
    title: "More",
    notify: 0
  },
]

function Navigation(flat = false, keyName){
  return (
  <nav className={styles.nav}>
    <NavigationButton>
      <Twitter />
    </NavigationButton>

    {MENU.map((menu) => (
       <NavigationButton 
       notify={menu.notify}
       selected={keyName.selectedKey === menu.key}>
         {menu.icon}
       <TextTitle>{menu.title}</TextTitle>
     </NavigationButton>
    ))}

  </nav>
  )
}

export default Navigation
