import React from "react";

import NavigationButton from "./navigation-button"
import TextTitle from "./text-title";
import styles from "./navigation.module.css"
import { Twitter, Home, Explore, Notification, Messages, Bookmark, Lists, Profile, More, HomeFill, ExplorerFill, NotificationFill, MessagesFill, BookmarkFill, ListsFill, ProfileFill } from "./icons"

const MENU = [
  {
    key: "twitter",
    icon: <Twitter />,
    iconSelected: <Twitter/>,
    title: "",
    notify: 0
  },
  {
    key: "home",
    icon: <Home />,
    iconSelected: <HomeFill />,
    title: "Home",
    notify: 0
  },
  {
    key: "explore",
    icon: <Explore />,
    iconSelected: <ExplorerFill />,
    title: "Explore",
    notify: 0
  },
  {
    key: "notification",
    icon: <Notification />,
    iconSelected: <NotificationFill />,
    title: "Notification",
    notify: 2
  },
  {
    key: "messages",
    icon: <Messages />,
    iconSelected: <MessagesFill />,
    title: "Messages",
    notify: 0
  },
  {
    key: "bookbark",
    icon: <Bookmark />,
    iconSelected: <BookmarkFill />,
    title: "Bookmark",
    notify: 0
  },
  {
    key: "lists",
    icon: <Lists />,
    iconSelected: <ListsFill />,
    title: "Lists",
    notify: 0
  },
  {
    key: "profile",
    icon: <Profile />,
    iconSelected: <ProfileFill />,
    title: "Profile",
    notify: 0
  },
  {
    key: "more",
    icon: <More />,
    title: "More",
    notify: 0
  },
]

function Navigation({ flat = false, selectedKey = "messages" }) {

  return (
    <nav className={styles.nav}>
      {MENU.map((menu) => {
        const showTitle = !flat && menu.title.length > 0
        const selected = selectedKey === menu.key
        return (
          <NavigationButton
            key={menu.key}
            notify={menu.notify}
            selected={selected}
          >
            {selected ? menu.iconSelected : menu.icon}
            {showTitle && <TextTitle>{menu.title}</TextTitle>}
          </NavigationButton>
        )
      })}
    </nav>
  )
}

export default Navigation
