import React from "react";
import cn from "classnames";
import Link from "next/link";
import styles from "./button.module.css";

function LinkButton({ href, children, ...props }){
  return (
  <Link href={href}>
    <a {...props}>
      {children}
    </a>
  </Link>
)};

function BaseButton({ children, ...props }){
  return (
    <button type="button" className={cn(styles.button, full && styles.fullWidth, className)} 
    {...props}
    >
      {children}
    </button>
)};

function Button({ href, full = false, children, className, ...props }){
  const Button = href ? LinkButton : BaseButton
  return (
    <Button 
      className={cn(styles.button, full && styles.fullWidth, className)} 
      {...props}
    >
      {children}
    </Button>
)};

export default Button
